function dayDifference(text) {
  var parsedDate = moment.utc(text);
  var now = moment.utc();
  var differenceInDays = now.diff(parsedDate, 'days'); //console.log(differenceInDays);

  if (differenceInDays < 30 && differenceInDays > 1) {
    return "".concat(differenceInDays, " days ago");
  } else if (differenceInDays == 1) {
    return 'yesterday';
  } else if (differenceInDays == 0) {
    // Calculate hours
    var differenceInHours = now.diff(parsedDate, 'hours'); //console.log(differenceInHours);

    if (differenceInHours > 0) {
      return "".concat(differenceInHours, " hours ago");
    } else {
      var differenceInMinutes = now.diff(parsedDate, 'minutes'); //console.log(differenceInMinutes);

      if (differenceInMinutes > 1) {
        return "".concat(differenceInMinutes, " mins ago");
      } else {
        var differenceInSec = now.diff(parsedDate, 'seconds'); //console.log(differenceInSec);

        return "".concat(differenceInSec, " secs ago");
      }
    }
  } else {
    return parsedDate.format('ddd, DD MMM YYYY HH:mm:ss') + ' GMT';
  }
}

