import React, { Component } from 'react';
const fetch = require('isomorphic-fetch');
import Router from 'next/router';

class Testimonial extends Component {
    constructor(props) {
        super(props);
        this.state = {
            product_testimonial: "",
        };
    }

    componentDidMount() {
        let id = Router.query.id;
        let splitData = id.split('-');
        var query = `
                    query ActivityById($id: ID!){
                        products(where:{id:$id}){
                            id,
                            product_name,
                            product_introduction,
                            product_hours,
                            product_type{
                              id,
                              product_type
                            },
                            course_stat{
                              id,
                              course_stat,
                            },
                            product_testimonials {
                              id,
                              ProductTestimonials {
                                id,
                                testimonial_description,
                                testimonial_full_name,
                                testimonial_founder_name,
                                 testimonial_image {
                                  url,
                                  name
                                },
                              }

                            }
                        }
                    }
                    `;
        var variables = {
            "id": Number(splitData[0])
        }
        fetch(process.env.GRAPHURL, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ query, variables }),
        })
            .then(response => response.json())
            .then((response) => {
                console.log('testi___', response);
                let productData = [];
                if (response.data.products) {
                    productData = response.data.products[0];

                    this.setState({
                        product_testimonial: productData.product_testimonials[0] ? productData.product_testimonials[0].ProductTestimonials : null,
                    });
                }

            });
        //console.log(homeBannerData);
    }
    render() {
        const { product_testimonial } = this.state;

        return (
            <>
                <section className="testimonial-wrap">
                    <div className="container">
                        <div className="row align-items-center justify-content-around">
                            <div className="col-md-7 col-xl-7">
                                <div className="row justify-content-center">
                                    <div className="col-xl-10 col-lg-11">
                                        <div className="h1 font-weight-light">
                                            {product_testimonial ?
                                                <q className="d-inline-block">
                                                    {product_testimonial.testimonial_description}
                                                </q>
                                                : 'No Data!'}
                                        </div>
                                        <div className="mb-2 font-weight-bold position-txt">{product_testimonial ? product_testimonial.testimonial_full_name + ',' + product_testimonial.testimonial_founder_name : ''}</div>
                                    </div>
                                </div>
                            </div>
                            <div className="col-md-4 col-lg-4 mb-md-0">
                                <div className="video-poster rounded mb-3">
                                    <a data-fancybox="" href="https://vimeo.com/421689971" className="btn btn-lg btn-primary btn-round">
                                        <svg width="24px" height="24px" viewBox="0 0 24 24" version="1.1" xmlns="http://www.w3.org/2000/svg" xmlnsXlink="http://www.w3.org/1999/xlink" className="injected-svg icon" data-src="http://54.255.146.92/wp-content/themes/kidocode/assets/img/icons/theme/media/play.svg" data-pagespeed-url-hash="4291238768">
                                            <title>Icon For Play</title>
                                            <g stroke="none" strokeWidth="1" fill="none" fillRule="evenodd">
                                                <rect opacity="0" x="0" y="0" width="24" height="24"></rect>
                                                <path d="M9.82866499,18.2771971 L16.5693679,12.3976203 C16.7774696,12.2161036 16.7990211,11.9002555 16.6175044,11.6921539 C16.6029128,11.6754252 16.5872233,11.6596867 16.5705402,11.6450431 L9.82983723,5.72838979 C9.62230202,5.54622572 9.30638833,5.56679309 9.12422426,5.7743283 C9.04415337,5.86555116 9,5.98278612 9,6.10416552 L9,17.9003957 C9,18.1765381 9.22385763,18.4003957 9.5,18.4003957 C9.62084305,18.4003957 9.73759731,18.3566309 9.82866499,18.2771971 Z" fill="#000000"></path>
                                            </g>
                                        </svg>
                                    </a>
                                    <img src={product_testimonial ? process.env.GRAPHIMAGEURL + product_testimonial.testimonial_image.url : "/assets/images/img01.png"} data-pagespeed-url-hash="3762136003" />
                                </div>
                            </div>
                        </div>
                    </div>
                </section>
            </>
        )
    }
}

export default Testimonial;
