import React, { Component } from 'react';
const fetch = require('isomorphic-fetch');
import Flickity from 'react-flickity-component';
import Router from 'next/router';
const flickityOptions = {
    initialIndex: 0,
    accessibility: true,
    pageDots: false,
    wrapAround: true
}
class Learning extends Component {
    constructor(props) {
        super(props);
        this.state = {
            relatedLearning: [],
            product_topics: [],
            relatedLearningData: '',
            productsGet: {
                type: "",
                data: []
            }
        };
    }

    componentDidMount() {
        let id = Router.query.id;
        let splitData = id.split('-');
        var query = `
        query {
            productTypes(where:{product_type:"Degree"}){
              id,
              product_type,
              product_topics{
                id,
                product_topic_name,
                product{
                  id,
                  product_name,
                  product_price,
                  product_slug,
                  product_type {
                    id,
                    product_type
                  },
                  product_topic {
                    id,
                    product_topic_name
                  },
                  product_media{
                    url,
                    name
                  },
                  product_sell_price
                }
              }
            }

        }
                    `;
        var variables = {
            "id": Number(splitData[0])
        }
        fetch(process.env.GRAPHURL, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ query, variables }),
        })
            .then(response => response.json())
            .then((response) => {
                console.log('--data', response)
                let getProductDataFilter = response.data.productTypes;
                // let relatedLearningData= getProductDataFilter[0];
                // let subTopicId= getProductDataFilter[0].product_topics[0].id
                let products = getProductDataFilter[0].product_topics[0].product.filter((item) => {
                    if (item.product_type != null) {
                        return (item.product_type.product_type === 'Degree') && (item.product_slug !== splitData[1] + '-' + splitData[2])
                    }
                })

                this.setState({
                    relatedLearning: getProductDataFilter,
                    product_topics: getProductDataFilter[0].product_topics,
                    productsGet: {
                        type: 'Degree',
                        data: products
                    }
                });
            });

        //console.log(homeBannerData);
    }
    getProductData = (product_topics, index) => {
        let id = Router.query.id;
        let splitData = id.split('-');
        let relatedLearning = this.state.relatedLearning;
        this.setState({
            productsGet: {
                type: product_topics,
                data: (relatedLearning && relatedLearning[0]) ? relatedLearning[0].product_topics[index].product.filter((item) => {
                    if (item.product_type != null) {
                        return (item.product_type.product_type === 'Degree') && (item.product_slug !== splitData[1] + '-' + splitData[2]) && (item.product_topic.product_topic_name === product_topics)
                    }
                }) : [],
            }
        })
    }
